import _ from 'lodash'

const modifyListData = (state, payload) => {
  state.transcriptionsData = payload
}

const deleteListItem = (state, id) => {
  state.transcriptionsData = _.remove(state.transcriptionsData, (arr) => {
    return arr.id !== id
  })
}

const addListItem = (state) => {
  const listItem = {
    id: _.random(100000000, 100000000000000),
    voice: '',
    text: ''
  }
  state.transcriptionsData.push(listItem)
}

const displayError = (state, err) => {
  state.errorMessage = err

  window.setTimeout(() => {
    state.errorMessage = ''
  }, 3000)
}

export default {
  modifyListData,
  deleteListItem,
  addListItem,
  displayError
}
